//#region src/shared/plugin.d.ts
interface MarkdownPowerPluginOptions {
  /**
   * 是否预设 markdown env，如 预设链接引用、缩写词、内容注释等
   *
   * 预设可以在任何 markdown 文件中使用
   *
   * @example
   * ```ts
   * env: {
   *   references: {
   *     vue: 'https://vuejs.org',
   *   },
   *   abbreviations: {
   *     vue: 'Vue.js',
   *   },
   *   annotations: {
   *     vue: 'Vue.js',
   *   },
   * }
   * ```
   * use `*.md`
   * ```markdown
   * [vue link][vue]
   * vue is a framework, [+vue] is modern framework
   * ```
   */
  env?: MarkdownEnvPreset;
  /**
   * 是否启用注释， 或者预设内容注释
   * @default false
   */
  annotation?: boolean | MarkdownEnvPreset['annotations'];
  /**
   * 是否启用 abbr 语法， 或者预设缩写词
   * @default false
   */
  abbr?: boolean | MarkdownEnvPreset['abbreviations'];
  /**
   * 马克笔动画模式
   * @default 'eager'
   */
  mark?: MarkOptions;
  /**
   * 配置代码块分组
   */
  codeTabs?: CodeTabsOptions;
  /**
   * 是否启用 npm-to 容器
   */
  npmTo?: boolean | NpmToOptions;
  /**
   * 是否启用 PDF 嵌入语法
   *
   * `@[pdf](pdf_url)`
   *
   * @default false
   */
  pdf?: boolean | PDFOptions;
  /**
   * 是否启用 图标支持
   * - iconify - `::collect:icon_name::` => `<VPIcon name="collect:icon_name" />`
   * - iconfont - `::name::` => `<i class="iconfont icon-name"></i>`
   * - fontawesome - `::fas:name::` => `<i class="fa-solid fa-name"></i>`
   *
   * @default false
   */
  icon?: IconOptions;
  /**
   * 是否启用 iconify 图标嵌入语法
   *
   * `::collect:icon_name::`
   *
   * @default false
   * @deprecated use `icon` instead 该配置已弃用，请使用 `icon` 代替
   */
  icons?: boolean | IconOptions;
  /**
   * 是否启用 隐秘文本 语法
   *
   * `!!plot_content!!`
   *
   * @default false
   */
  plot?: boolean | PlotOptions;
  /**
   * 是否启用 timeline 语法
   *
   * ```md
   * ::: timeline
   * - title
   *   time="Q1" icon="ri:clockwise-line" line="dashed" type="warning" color="red"
   *
   *   xxx
   * :::
   * ```
   *
   * @default false
   */
  timeline?: boolean;
  /**
   * 是否启用 collapse 折叠面板 语法
   *
   * ```md
   * ::: collapse accordion
   * - + title
   *
   *   content
   *
   * - - title
   *
   *   content
   * :::
   * ```
   *
   * @default false
   */
  collapse?: boolean;
  /**
   * 是否启用 chat 容器 语法
   *
   * ```md
   * ::: chat
   * {:date}
   *
   * {user}
   * message
   *
   * {.}
   * message
   * :::
   * ```
   * @default false
   */
  chat?: boolean;
  /**
   * 是否启用 field / field-group 容器
   *
   * @default false
   */
  field?: boolean;
  /**
   * 是否启用 acfun 视频嵌入
   *
   * `@[acfun](acid)`
   *
   * @default false
   */
  acfun?: boolean;
  /**
   * 是否启用 bilibili 视频嵌入
   *
   * `@[bilibili](bid)`
   *
   * @default false
   */
  bilibili?: boolean;
  /**
   * 是否启用 youtube 视频嵌入
   *
   * `@[youtube](video_id)`
   *
   * @default false
   */
  youtube?: boolean;
  /**
   * 是否启用 artPlayer 视频嵌入
   *
   * `@[artPlayer](url)`
   */
  artPlayer?: boolean;
  /**
   * 是否启用 audioReader 音频嵌入
   *
   * `@[audioReader](url)`
   */
  audioReader?: boolean;
  /**
   * 是否启用 codepen 嵌入
   *
   * `@[codepen](pen_id)`
   *
   * @default false
   */
  codepen?: boolean;
  /**
   * @deprecated
   */
  replit?: boolean;
  /**
   * 是否启用 codeSandbox 嵌入
   *
   * `@[codesandbox](codesandbox_id)`
   *
   * @default false
   */
  codeSandbox?: boolean;
  /**
   * 是否启用 jsfiddle 嵌入
   *
   * `@[jsfiddle](jsfiddle_id)`
   *
   * @default false
   */
  jsfiddle?: boolean;
  /**
   * 是否启用 REPL 容器语法
   *
   * @default false
   */
  repl?: false | ReplOptions;
  /**
   * 是否启用 文件树 容器语法
   *
   * @default false
   */
  fileTree?: boolean | FileTreeOptions;
  /**
   * 是否启用 代码树 容器语法 和 嵌入语法
   *
   * ```md
   * ::: code-tree
   * :::
   * ```
   *
   * `@[code-tree](file_path)`
   *
   *
   * @default false
   */
  codeTree?: boolean | CodeTreeOptions;
  /**
   * 是否启用 demo 语法
   */
  demo?: boolean;
  /**
   * 是否启用 caniuse 嵌入语法
   *
   * `@[caniuse](feature_name)`
   *
   * @default false
   */
  caniuse?: boolean | CanIUseOptions;
  /**
   * 是否启用 table 容器语法，为表格提供增强功能
   *
   * - `copy`: 是否启用复制功能，支持复制为 html 格式 和 markdown 格式
   *
   * @default false
   */
  table?: boolean | TableContainerOptions;
  /**
   * 是否启用 二维码 嵌入语法
   *
   * @default false
   */
  qrcode?: boolean;
  /**
   * 是否启用 自动填充 图片宽高属性
   *
   * __请注意，无论是否启用，该功能仅在构建生产包时生效__
   *
   * - 如果为 `true` ，等同于 `'local'`
   * - 如果为 `local`，则仅对本地图片 添加 width 和 height
   * - 如果为 `all`，则对所有图片(即包括 本地 和 远程) 添加 width 和 height
   *
   * 图片在加载过程中如果比较慢，从加载到完成的过程会导致页面布局不稳定，导致内容闪烁等。
   * 此功能通过给图片添加 `width` 和 `height` 属性来解决该问题。
   *
   * 请谨慎使用 `all` 选项，该选项会在构建阶段发起网络请求，尝试加载远程图片以获取图片尺寸信息，
   * 这可能会导致 构建时间变得更长（幸运的是获取尺寸信息只需要加载图片 几 KB 的数据包，因此耗时不会过长）
   *
   * @default false
   */
  imageSize?: boolean | 'local' | 'all';
}
